mapper.select:
select b.id, b.title, b.update_time, b.recommend, b.type_id, b.published, t.id `type.id`, t.name `type.name` from t_blog b, t_type t
b.type_id = t.id
and title like concat('%',#{title},'%')
and type_id = #{typeId}
and recommend = #{recommend}
order by update_time desc

mapper.update:
update t_blog
set views = views + 1
where id = #{id};
